INSERT INTO `tb_post` (`id`, `header`, `imageHeader`, `detailHeader`, `content`, `date`, `status`, `id_page`) VALUES
(1, 'น้ำผลไม้โมนาวี ออริจินอล หรือ โมนาวี เอสแซนเชียล [ MonaVie Original / Essential ]', '1e363866e496b7.jpg', 'น้ำผลไม้โมนาวี ออริจินอล หรือ โมนาวี เอสแซนเชียล [ MonaVie Original / Essential ]', '<p style="text-align:center"><img alt="" src="http://t2.gstatic.com/images?q=tbn:ANd9GcQvSQLuTOY3LmqX-jtqPrGVdlz2nNCkdfljUdQNbQuWBxRUvG6C" style="height:273px; width:645px" /></p>\r\n\r\n<h1><span style="color:#800080">น้ำผลไม้โมนาวี ออริจินอล หรือ โมนาวี เอสแซนเชียล [&nbsp;MonaVie Original / Essential&nbsp;]</span></h1>\r\n\r\n<p><img alt="" src="http://c.lnwfile.com/_resize_images/300/300/yf/o8/hv.jpg" style="float:left; height:300px; width:300px" /></p>\r\n\r\n<p><span style="font-size:14px">โมนาวี ออริจินอล หรือ เอสแซนเชียล / (MonaVie Original/Essential)&nbsp;จากผลอาซาอี a&ccedil;ai&nbsp;</span></p>\r\n\r\n<p><span style="font-size:14px">โมนาวี เอสแซนเชียล MonaVie Essential ออกแบบมาเพื่อต่อต้านอนุมูลอิสระโดยเฉพาะ โมนาวี เอสเซนเชียล ประกอบด้วยผลไม้ 19 ชนิดที่ผสมอย่างลงตัวที่ช่วยสนับสนุนสารอาหารที่ร่างกายต้องการ น้ำผลไม้ที่มีประสิทธิภาพ ต่อต้านความชรา และช่วยเกี่ยวกับสุขภาพโดยรวม ด้วยพลังการต่อต้านอนุมูลอิสระ ง่ายๆเพียงดื่ม โมนาวี เอสเซนเชียล</span></p>\r\n\r\n<p><span style="font-size:14px">ซึ่งผลิตภัณฑ์ โมนาวี ทุกสูตร มีส่วนประกอบหลักจากผลไม้ 19 ชนิดดังต่อไปนี้&nbsp;</span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table>\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<ul>\r\n				<li><span style="font-size:14px">Acai Berry อาไซอิ เบอร์รี่&nbsp;</span></li>\r\n				<li><span style="font-size:14px">Pomegranate ผลทับทิม</span></li>\r\n				<li><span style="font-size:14px">Billberry บิลเบอร์รี่</span></li>\r\n				<li><span style="font-size:14px">Passion Fruit เพรสชั่นฟรุ๊ต&nbsp;</span></li>\r\n				<li><span style="font-size:14px">wolfberry วูลเบอร์รี่</span></li>\r\n				<li><span style="font-size:14px">Blueberry บูลเบอร์รี่</span></li>\r\n				<li><span style="font-size:14px">Kiwi ผลกีวี</span></li>\r\n				<li><span style="font-size:14px">Cranberry แครนเบอร์รี่</span></li>\r\n				<li><span style="font-size:14px">Apricot แอพริคอท</span></li>\r\n				<li><span style="font-size:14px">Prune ลูกพรุน</span></li>\r\n			</ul>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><span style="font-size:14px">Nashi ลูกสาลี่</span></li>\r\n				<li><span style="font-size:14px">Aronia อโรเนีย&nbsp;</span></li>\r\n				<li><span style="font-size:14px">Camu Camu กามู กามู</span></li>\r\n				<li><span style="font-size:14px">Acerola อะเซโรล่า</span></li>\r\n				<li><span style="font-size:14px">Banana ผลกล้วย</span></li>\r\n				<li><span style="font-size:14px">Pear ลูกแพร</span></li>\r\n				<li><span style="font-size:14px">Lychee ลิ้นจี่</span></li>\r\n				<li><span style="font-size:14px">White Grape องุ่นขาว</span></li>\r\n				<li><span style="font-size:14px">Purple Grape องุ่นดำ&nbsp;</span></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', '2013-09-19 04:47:08', '1', 1),
(2, 'ภาพรวมบริษัทโมนาวี', '5deb1206fc7ad7.png', 'โมนาวีรวมพลังคุณค่าทางโภชนาการต้านทานอนุมูลอิสระเข้ากับโอกาสธุรกิจที่ไม่มีใครเหมือน', '<div class="sub-header" style="margin: 0px 0px 40px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; position: relative; color: rgb(255, 255, 255); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<h2><span style="color:#000000">ภาพรวมบริษัทโมนาวี</span></h2>\r\n</div>\r\n\r\n<div class="generic-content" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<div class="img" style="margin: 0px; padding: 0px; border: 0px; font-size: 17px; font: inherit; vertical-align: baseline; outline: none; "><img src="http://th.monavie.com/sites/th.monavie.com/files/styles/generic_page_image_layout_1/public/managed/generic/MonaVie%20Building_Web%20685%20x%20400_1_1.png" style="border:0px; font-size:17px; font:inherit; margin:0px; outline:none; padding:0px; vertical-align:baseline" /></div>\r\n\r\n<p>โมนาวีรวมพลังคุณค่าทางโภชนาการต้านทานอนุมูลอิสระเข้ากับโอกาสธุรกิจที่ไม่มีใครเหมือน เพื่อให้ท่านได้เสริมสร้างสุขภาพที่ดีและมีธุรกิจเกี่ยวกับสุขภาพ เราเปิดตัวในปีพ.ศ. 2548 นำเสนอผลิตภัณฑ์เพื่อสุขภาพคุณภาพเยี่ยมที่ผลิตมาเพื่อเสริมสุขภาพผู้คนนับล้านและเพื่อเปลี่ยนแปลงชีวิตครอบครัวโมนาวีไปในทางที่ดีขึ้น</p>\r\n\r\n<p>โมนาวีเป็นหนึ่งในบริษัทที่เติบโตเร็วที่สุดในโลก (โมนาวีมีชื่ออยู่ในการจัดอันดับ 500 บริษัทที่เติบโตเร็วที่สุดโดยนิตยสาร Inc. ประจำปีพ.ศ. 2552) ทั้งยังมีแผนขยายกิจการไปในสหรัฐอเมริกาและทั่วโลก และเรายังมีแนวคิดที่จะช่วยให้ผู้คนจากหลากหลายพื้นเพได้ประสบความสำเร็จ เป้าหมายของโมนาวีไม่ได้หยุดอยู่แค่การขายผลิตภัณฑ์ หากแต่อยู่ที่การมอบความหวังและเป็นที่พึ่งให้ผู้คนสามารถมีรายได้</p>\r\n\r\n<p>ความสำเร็จที่ได้รับไม่ได้เป็นแค่ความสำเร็จของโมนาวีเพียงอย่างเดียว ความสำเร็จที่แท้จริงคือการที่นักธุรกิจได้แบ่งปันผลิตภัณฑ์โมนาวีและแนวทางการใช้ชีวิตที่ดีต่อสุขภาพยิ่งขึ้น พร้อมไปกับการมีรายได้ นี่คือโอกาสที่ท่านจะได้แบ่งปันผลิตภัณฑ์ชั้นเยี่ยมพร้อมทั้งเสริมสร้างรายได้ ไม่ว่าท่านสนใจจะเปลี่ยนแปลงชีวิต หรือเพิ่มรายได้ ท่านก็สามารถเป็นส่วนหนึ่งของกลุ่มนักธุรกิจโมนาวีที่ประสบความสำเร็จนับแสนคน ผู้ได้พบกับความเปลี่ยนแปลงผ่านทางผลิตภัณฑ์เพื่อสุขภาพคุณภาพเยี่ยม</p>\r\n\r\n<p>ด้านล่างคือรายชื่อสื่อที่ได้จัดอันดับและพูดถึงโมนาวี หรือเรียกดูแผนการรับรายได้บริษัทโมนาวี</p>\r\n\r\n<ul>\r\n	<li>อันดับที่ 18 ในการจัดอันดับ 500 บริษัทที่เติบโตเร็วที่สุดโดยนิตยสาร Inc. และอันดับ 1 ในหมวดอาหารและเครื่องดื่ม</li>\r\n	<li>กลุ่มบริษัทที่มีมูลค่ากิจการร้อยล้านเหรียญสหรัฐประจำปี 2552 ในนิตยสารไดเร็คเซลลิ่งนิวส์</li>\r\n	<li>กลุ่มบริษัทที่มีมูลค่ากิจการร้อยล้านเหรียญสหรัฐประจำปี 2553 ในนิตยสารไดเร็คเซลลิ่งนิวส์</li>\r\n	<li>รางวัลผู้ประกอบการยอดเยี่ยมแห่งปีจากบริษัทเอินส์ท แอนด์ ยัง ที่มอบให้ดาลลิน เอ. ลาร์สัน</li>\r\n	<li>รางวัลประธานกรรมการบริหารแห่งปี โดยนิตยสารยูทาห์บิสซิเนส</li>\r\n	<li>บริษัทที่เติบโตเร็วที่สุดในรัฐยูทาห์ โดยเมาน์เทนเวสแคพิทอลเน็ตเวิร์ค</li>\r\n	<li>อันดับ 1 ในการจัดอันดับโดยนิตยสารยูทาห์บิสซิเนส ฉบับ 50 บริษัทที่เติบโตเร็วที่สุด</li>\r\n	<li>รายการเอ็มทีวีคริบส์ สถานีโทรทัศน์เอ็มทีวี</li>\r\n	<li>รายการเดอะ ดอกเตอร์สฺ สถานีโทรทัศน์ซีบีเอส</li>\r\n	<li>รายการเรเชล เรย์</li>\r\n	<li>รายการทูเดย์โชว์ สถานีโทรทัศน์เอ็นบีซี</li>\r\n	<li>นิตยสารฟอร์จูน</li>\r\n	<li>การจัดอันดับ 500 บริษัทที่มีการเจริญเติบโตเร็วที่สุดโดยนิตยสาร Inc.</li>\r\n	<li>นิตยสารโว้ค</li>\r\n	<li>เว็บไซท์ของนิตยสารพีเพิล</li>\r\n	<li>นิตยสารบิสซิเนสวีค</li>\r\n</ul>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<div class="header" style="margin: 0px; padding: 0px 0px 10px; border-width: 0px 0px 1px; border-bottom-style: solid; border-bottom-color: rgb(238, 238, 238); font: inherit; vertical-align: baseline; outline: none; position: relative; color: rgb(255, 255, 255); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<h1><span style="color:#000000">โครงการมอร์</span>ส่งมอบความอุดมสมบูรณ์กลับสู่สังคม</h1>\r\n</div>\r\n\r\n<div class="main" style="margin: 55px 0px 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; position: relative; color: rgb(255, 255, 255); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<div class="sub-page" style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: none; width: 685px; min-height: 800px; ">\r\n<div class="generic-content" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); ">\r\n<div class="img" style="margin: 0px; padding: 0px; border: 0px; font-size: 17px; font: inherit; vertical-align: baseline; outline: none; "><img src="http://th.monavie.com/sites/th.monavie.com/files/styles/generic_page_image_layout_1/public/managed/generic/MORE%20Project_780%20x%20300.png" style="border:0px; font-size:17px; font:inherit; margin:0px; outline:none; padding:0px; vertical-align:baseline" /></div>\r\n\r\n<p>หลังจากเปิดบริษัทในปี 2548 ได้ไม่นาน โมนาวีก็ได้รับการตอบรับอย่างดีเยี่ยม เห็นได้จากยอดขายถล่มทลาย ดาลลิน เอ. ลาร์สัน ผู้ก่อตั้งและประธานกรรมการบอร์ดบริหาร จึงเกิดความคิดการจัดตั้งโครงการความรับผิดชอบต่อสังคมเพื่อสร้างความเปลี่ยนแปลงในชีวิตเด็กยากไร้ จนได้มาพบกับดั๊ก รอวแลนด์ สมาชิกของบริษัทผู้มีส่วนร่วมในการช่วยเหลือสังคมในประเทศบราซิล และเซอร์จิโอ ป๊องซ์ ผู้มีคุณแม่เป็นหลักสำคัญของการช่วยเหลือผู้ยากไร้ในย่านสลัมของเมืองริโอเดอจาเนโร เมื่อดาลลินและคาร์รี ภรรยา ได้พบกับเซอร์จิโอก็สัมผัสได้ถึงความรักและความมุ่งมั่นในพันธะสัญญาเพื่อช่วยเหลือชุมชนยากไร้ในเมืองริโอเดอจาเนโร ดาลลินจึงสนับสนุนโดยจัดตั้งโครงการความรับผิดชอบต่อสังคมอย่างเป็นทางการตั้งแต่บัดนั้นเป็นต้นมา</p>\r\n\r\n<p>โครงการมอร์ประเทศไทย<br />\r\nโครงการมอร์ประเทศไทยก่อตั้งขึ้นเมื่อปี 2555 โดยเริ่มโครงการการให้ความช่วยเหลือระยะยาวกับโรงเรียนพุทธเกษตร อำเภอขุนยวม จังหวัดแม่ฮ่องสอน ครอบครัวจากหลากหลายชุมชนชาวเผ่าของภาคเหนือผู้ใช้ชีวิตความเป็นอยู่อย่างยากจนและอาศัยอยู่อย่างแร้นแค้นบนที่ดินผืนเล็กๆ ต่างก็เดินทางมารับความช่วยเหลือที่โรงเรียนแห่งนี้&nbsp; หากกล่าวถึงเรื่องการศึกษา โรงเรียนพุทธเกษตรถือเป็นสถานที่เดียวที่พวกเขาจะได้รับโอกาสนั้น เพื่อลดภาระค่าใช้จ่ายของครอบครัวที่มีอยู่อย่างไม่มากเป็นทุนเดิม ทางโรงเรียนจึงมอบโอกาสและยกระดับชีวิตความเป็นอยู่ด้านอาหาร ทุนการศึกษา ซึ่งชุมชนชาวเผ่าเหล่านี้ต่างก็รับรู้ถึงการช่วยเหลืออย่างแท้จริง</p>\r\n\r\n<p>โรงเรียนพุทธเกษตรให้ความช่วยเหลือในเรื่องของที่พักพิง อาหาร คุณภาพชีวิต การศึกษา และทักษะทางการเกษตรให้กับเด็กๆ กว่าร้อยชีวิต</p>\r\n\r\n<p><a href="http://www.themoreprojectthailand.com/" style="margin: 0px; padding: 0px; border: 0px; font-size: 17px; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(126, 162, 0); text-decoration: none; ">เข้าสู่เว็บไซต์โครงการมอร์</a></p>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n\r\n<div id="wrchoverdiv" style="display: none; ">\r\n<div id="wrccontainer">\r\n<div id="wrcheader">\r\n<div id="wrctitle">WebRep</div>\r\n</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n\r\n<div id="wrccurrentvote">currentVote</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n\r\n<div id="wrcrating">&nbsp;</div>\r\n\r\n<div id="wrcratingtext">noRating</div>\r\n\r\n<div id="wrcweighttext">noWeight</div>\r\n\r\n<div id="wrcflags">\r\n<div class="wrcicon" id="wrcicon_shopping">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_social">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_news">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_it">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_corporate">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_pornography">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_violence">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_gambling">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_drugs">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_illegal">&nbsp;</div>\r\n</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n</div>\r\n</div>\r\n', '2013-09-19 04:48:30', '1', 2),
(3, 'Hello Team', '87f8ac7d5ec2d6.jpg', 'Hello Why Team 2PM4You', '<h1>Hello Why Team 2PM4You<br />\r\n<span style="background-color:transparent; color:rgb(32, 32, 32); font-size:14px">&nbsp;</span></h1>\r\n\r\n<h1><span style="background-color:transparent; color:rgb(32, 32, 32); font-size:14px">เกิดจากการการเป็นโอกาสอันยิ่งใหญ่ในการสร้างธุรกิจที่มั่นคงกับบริษัทเครือ ข่ายยักใหญ่ระดับโลก ซึ่งนับ 10 ปีแล้วที่บริษัทระดับโลกอย่างโมนาวี ไม่ได้เข้ามาทำธุรกิจในประเทศไทย มีแค่บริษัทขนาดกลางและขนาดเล็กเท่านั้นที่เข้ามาดำเนินธุรกิจในประเทศไทย และด้วยปัญหาในเรื่องต่างๆจากความไม่พร้อมของบริษัทเหล่านั้น ทำให้นักธุรกิจต้องประสบปัญหาในการทำธุรกิจและกระทบต่อการเติบโตขององค์กร จนกระทั่งหลายๆบริษัทต้องปิดตัวลง ทำให้นักธุรกิจมากมายต้องกระจัดกระจาย ระเหเร่ร่นไปหาบริษัทใหม่ และเป็นเช่นนี้ซ้ำๆเรื่อยมา ทำให้นักธุรกิจหลายๆคนที่เคยเชื่อว่าธุรกิจเครือข่ายสามารถสร้างชีวิตหรือ เปลี่ยนชีวิตได้ กลับรู้สึกว่านักธุรกิจนี้เป็นธุรกิจที่มีความเสี่ยงสูง และไม่มั่นคงเลย</span></h1>\r\n\r\n<div id="wrchoverdiv" style="display: none; ">\r\n<div id="wrccontainer">\r\n<div id="wrcheader">\r\n<div id="wrctitle">WebRep</div>\r\n</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n\r\n<div id="wrccurrentvote">currentVote</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n\r\n<div id="wrcrating">&nbsp;</div>\r\n\r\n<div id="wrcratingtext">noRating</div>\r\n\r\n<div id="wrcweighttext">noWeight</div>\r\n\r\n<div id="wrcflags">\r\n<div class="wrcicon" id="wrcicon_shopping">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_social">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_news">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_it">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_corporate">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_pornography">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_violence">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_gambling">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_drugs">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_illegal">&nbsp;</div>\r\n</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n</div>\r\n</div>\r\n', '2013-09-19 04:49:32', '1', 3),
(4, 'แผนการตลาด/แผนรายได้โมนาวี', '', '', '<h2>แผนการตลาด/แผนรายได้โมนาวี</h2>\r\n\r\n<p>เป็นแผนแบบไบนารี่ที่ง่ายแสนง่าย ท่านสร้างทีมงานเริ่มต้นเพียงแต่ 2 คนก็สามารถสร้างรายได้มหาศาลได้แล้ว อีกทั้งเป็นแผนการตลาดที่ทำให้คุณมีรายได้ถึง 9 ช่องทาง เริ่มต้นด้วยการแผนการตลาดสมาชิกเพียง 500 บาท และซื้อสินค้าทำธุรกิจ 3 แบบ ดังนี้</p>\r\n\r\n<p>1. สินค้า 2 ขวด 50 คะแนน<br />\r\n2. สินค้า 4 ขวด 100 คะแนน<br />\r\n3. สินค้า 8 ขวด 200 คะแนน</p>\r\n', '2013-09-15 08:15:01', '1', 4),
(5, 'Maldives Gold Getaway—รายชื่อนักธุรกิจที่ผ่านคุณสมบัติออกแล้ว', '5afed1e44905f5.jpg', 'รางวัลท่องเที่ยวสุดพิเศษสำหรับนักธุรกิจที่ดำรงคุณสมบัติโกลด์ (Gold) สองสัปดาห์ (“Paid-as” Gold) ในช่วงเวลาที่กำหนด', '<p>NOV 2, 2012</p>\r\n\r\n<h1>Maldives Gold Getaway&mdash;รายชื่อนักธุรกิจที่ผ่านคุณสมบัติออกแล้ว</h1>\r\n\r\n<div class="categories" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><a href="http://th.monavie.com/th/news/________________________" style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(126, 162, 0); text-decoration: none; ">อีเว้นท์</a></div>\r\n\r\n<div class="main-img" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><img alt="" src="http://th.monavie.com/sites/th.monavie.com/files/styles/media_blog_image_layout/public/managed/blog/thaildand-gold-getaway_0.jpg" style="border:0px; font-size:12px; font:inherit; margin:0px; outline:none; padding:0px; vertical-align:baseline" title="" /></div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<p><span style="font-size:14px"><strong><a href="http://th.monavie.com/sites/th.monavie.com/files/TH_Maldives%20Gold%20Getaway_Qualifiers%20List_20130314_Thai.pdf" style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(126, 162, 0); text-decoration: none; ">คลิกที่นี่</a></strong>เพื่อเรียกดูรายชื่อนักธุรกิจที่ผ่านคุณสมบัติโปรโมชั่นเดินทางสัมมนานี้</span></p>\r\n\r\n<p><span style="font-size:14px">รางวัลท่องเที่ยวสุดพิเศษสำหรับนักธุรกิจที่ดำรงคุณสมบัติโกลด์ (Gold) สองสัปดาห์ (&ldquo;Paid-as&rdquo; Gold) ในช่วงเวลาที่กำหนด<br />\r\nเดินทางท่องเที่ยวเกาะมัลดีฟส์ ดินแดนแห่งสวรรค์กลางน้ำ สถานที่สวยงามที่สุดแห่งหนึ่งของโลก</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>ระยะเวลาการทำคุณสมบัติ:</strong>&nbsp;3 พฤศจิกายน 2555-16 มีนาคม 2556 (รอบคำนวณโบนัสที่ 410&ndash;428)<br />\r\n<strong>รวมระยะเวลาการทำคุณสมบัติ:</strong>&nbsp;19 รอบคำนวณโบนัส<br />\r\n<strong>กำหนดการเดินทาง:&nbsp;</strong>พฤษภาคม 2556</span></p>\r\n\r\n<p><a class="generic-button" href="http://media.monavie.com/PDF/thailand/TH_Maldives%20Gold%20Getaway_Poster_Thai.pdf" style="margin: 20px auto 0px; padding: 4px 20px; border: none; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(255, 255, 255); text-decoration: none; height: 24px; font-family: futura-pt-n5, futura-pt, Arial, sans-serif; text-align: center; text-transform: uppercase; line-height: 18px; cursor: pointer; background-image: -webkit-linear-gradient(top, rgb(232, 255, 127) 0%, rgb(181, 218, 12) 50%, rgb(121, 139, 38) 100%); border-top-left-radius: 3px; border-top-right-radius: 3px; border-bottom-right-radius: 3px; border-bottom-left-radius: 3px; ">เรียกดูใบปลิว</a></p>\r\n\r\n<p><span style="font-size:14px"><strong>รายละเอียดการทำคุณสมบัติ</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">ต้องดำรงคุณสมบัติโกลด์ (Gold) สองสัปดาห์ (&ldquo;Paid-as&rdquo; Gold) ในช่วงเวลาที่กำหนด โดยคุณสมบัติโกลด์ (Gold) มีดังต่อไปนี้</span></li>\r\n	<li><span style="font-size:14px">ต้องมีคะแนนอย่างน้อยข้างละ 5,000 PV</span></li>\r\n	<li><span style="font-size:14px">ต้องมียอดส่วนตัวอย่างน้อย 100 PV (Active) ในช่วงเวลาที่กำหนด</span></li>\r\n	<li><span style="font-size:14px">ต้องมีการแนะนำตรง ซ้าย 1 ขวา 1 ที่มียอดส่วนตัวอย่างน้อย<span dir="rtl">&nbsp;</span>50 PV (Qualified)</span></li>\r\n	<li><span style="font-size:14px">ต้องสปอนเซอร์ตรงนักธุรกิจใหม่ 5 ท่าน โดยต้องมีการสั่งซื้อครั้งแรกด้วยชุดผลิตภัณฑ์ดูแลผิวโมนาวีเอ็นวี (MonaVie NV Collection Set) ขั้นต่ำ 100 PV (Active) ในช่วงเวลาที่กำหนด</span></li>\r\n	<li><span style="font-size:14px">ต้องมียอดส่วนตัวอย่างน้อย 100 PV (Active) ในช่วงเวลาที่กำหนดและตลอดจนวันเดินทาง</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>เงื่อนไขการเข้าร่วม</strong><strong>เดินทางท่องเที่ยว</strong><strong>เกาะ</strong><strong>มัลดีฟส์</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">นักธุรกิจโมนาวีที่ได้รับรางวัลท่องเที่ยวต้องเป็นผู้ที่ปฎิบัติตามกฎ จรรยาบรรณ เงื่อนไข และข้อบังคับของทางบริษัทอย่างถูกต้อง</span></li>\r\n	<li><span style="font-size:14px">บริษัทจะยืนยันสิทธิ์ในการเข้าร่วมทริปของนักธุรกิจหลังจากที่ได้ตรวจสอบอย่างละเอียดแล้วเท่านั้น</span></li>\r\n	<li><span style="font-size:14px">การเข้าร่วมทริปสงวนสิทธิ์สูงสุด 2 ที่นั่งต่อรหัสเท่านั้น โดยสามารถมอบสิทธิ์ที่ 2 ให้กับสมาชิกในครอบครัวได้เท่านั้น</span></li>\r\n	<li><span style="font-size:14px">การจะได้รับสิทธิ์ในที่นั่งที่ 2 ท่านต้องดำรงคุณสมบัติโกลด์ (Gold) อย่างน้อยอีกสองสัปดาห์ (&ldquo;Paid-as&rdquo; Gold) ในช่วงเวลาที่กำหนด</span></li>\r\n	<li><span style="font-size:14px">การขึ้นตำแหน่งที่สูงกว่าโกลด์ (Gold) การทำยอดและการสปอนเซอร์ตรงนักธุรกิจใหม่ได้มากกว่าเป็นหลายเท่าตัวไม่มีผลต่อจำนวนที่นั่งที่จะได้รับ</span></li>\r\n	<li><span style="font-size:14px">ผู้ที่จะเดินทางต้องเป็นเจ้าของรหัสนักธุรกิจที่ผ่านคุณสมบัติการเข้าร่วมทริปเท่านั้น ไม่สามารถโอนสิทธิ์ให้ผู้อื่นหรือเปลี่ยนเป็นเงินสดได้</span></li>\r\n	<li><span style="font-size:14px">การทำคุณสมบัตินับเฉพาะคะแนนที่เกิดขึ้นในประเทศไทยเท่านั้น</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>หมายเหตุ</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">นักธุรกิจที่ผ่านคุณสมบัติการเข้าร่วมทริปจะถูกหักภาษี ณ ที่จ่ายจากมูลค่ารางวัลท่องเที่ยว</span></li>\r\n	<li><span style="font-size:14px">ผู้ประสงค์เข้าร่วมทริปต้องยื่นหนังสือแจ้งความจำนงภายในวันเสาร์ที่ 16 มีนาคม 2556 มิฉะนั้นจะถือว่าท่านไม่ได้ทำคุณสมบัติครบถ้วนตามกำหนด&nbsp;<strong><a href="http://media.monavie.com/PDF/thailand/TH_Maldives%20Gold%20Getaway_Letter%20of%20Intent_Thai.pdf" style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(126, 162, 0); text-decoration: none; ">คลิกที่นี่</a>เพื่อดาวน์โหลดหนังสือแจ้งความจำนง</strong></span></li>\r\n	<li><span style="font-size:14px">หากท่านผ่านคุณสมบัติแต่ไม่ต้องการเข้าร่วมทริป ท่านต้องแจ้งล่วงหน้าอย่างน้อย 15 วันก่อนวันเดินทาง หากท่านไม่ปฏิบัติตาม ท่านจะถูกตัดสิทธิ์ในการเข้าร่วมทริปครั้งต่อไป</span></li>\r\n	<li><span style="font-size:14px">หากท่านไม่สามารถเข้าร่วมทริปได้ไม่ว่าด้วยกรณีใดก็ตาม บริษัทขอสงวนสิทธิ์ในการชดเชยเป็นเงินสด</span></li>\r\n	<li><span style="font-size:14px">บริษัทขอสงวนสิทธิ์ในการพิจารณาสิทธิ์ในการเข้าร่วมทริปแต่เพียงผู้เดียว</span></li>\r\n	<li><span style="font-size:14px">บริษัทขอสงวนสิทธิ์ในการเปลี่ยนแปลงรายละเอียดและเงื่อนไขโดยไม่ต้องแจ้งให้ทราบล่วงหน้า</span></li>\r\n</ul>\r\n</div>\r\n\r\n<p><span style="font-size:14px"><span style="color:rgb(86, 86, 86); font-family:futura-pt-n4,futura-pt,arial,sans-serif">- See more at: http://th.monavie.com/th/news/view/maldives-gold-getaway#sthash.YfNU4fu3.dpuf</span></span></p>\r\n\r\n<div id="wrchoverdiv" style="display: none; ">\r\n<div id="wrccontainer">\r\n<div id="wrcheader">\r\n<div id="wrctitle">WebRep</div>\r\n</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n\r\n<div id="wrccurrentvote">currentVote</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n\r\n<div id="wrcrating">&nbsp;</div>\r\n\r\n<div id="wrcratingtext">noRating</div>\r\n\r\n<div id="wrcweighttext">noWeight</div>\r\n\r\n<div id="wrcflags">\r\n<div class="wrcicon" id="wrcicon_shopping">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_social">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_news">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_it">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_corporate">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_pornography">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_violence">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_gambling">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_drugs">&nbsp;</div>\r\n\r\n<div class="wrcicon" id="wrcicon_illegal">&nbsp;</div>\r\n</div>\r\n\r\n<div class="wrchorizontal">&nbsp;</div>\r\n</div>\r\n</div>\r\n', '2013-09-19 04:50:58', '1', 5);
INSERT INTO `tb_post` (`id`, `header`, `imageHeader`, `detailHeader`, `content`, `date`, `status`, `id_page`) VALUES
(6, 'แนวคิดเงินสี่ด้าน', '0cac7e2851b6d9.jpg', 'คนที่รวยที่สุดในโลกล้วนแสวงหา การสร้างเครือข่าย ในขณะที่ผู้คนส่วนใหญ่กำลังหางานทำ', '<h1><span style="background-color:transparent">แนวคิดเงินสี่ด้าน</span></h1>\r\n\r\n<p><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif"><span style="background-color:transparent"><span style="background-color:transparent"><span style="background-color:transparent">คนที่รวยที่สุดในโลกล้วนแสวงหา การสร้างเครือข่าย ในขณะที่ผู้คนส่วนใหญ่กำลังหางานทำ หากคุณมีความคิดสร้างสรรอันยิ่งใหญ่ หรือสินค้าอันดีเยี่ยมปานใดก็ตาม มีเพียงหนทางเดียวที่จะนำท่านสู่ความสำเร็จ คือ การใช้เครือข่ายการประชาสัมพันธ์ และเครือข่ายการ จัดจำหน่ายสินค้า เหล่านั้นสู่มือผู้บริโภคอย่างได้ผล</span></span></span></span></span></p>\r\n\r\n<table align="center" border="0" cellpadding="1" cellspacing="1" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style="font-size:14px"><span style="color:rgb(255, 102, 0); font-family:arial,helvetica,sans-serif"><strong>E (Employee) - ลูกจ้าง</strong></span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- รับค่าตอบแทนเป็นเงินเดือน</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- รายได้ตามตำแหน่งงานที่ได้รับมอบหมาย</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- นายจ้างเป็นผู้กำหนดวิถีชีวิตคุณ</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ขาดอิสรภาพ ต้องเซ็นต์ชื่อ ตอกบัตร</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ตกงานเท่ากับล้มละลาย</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- อยู่ในวงจรหนี้สิน ผ่อนบ้าน ผ่อนรถ ฯลฯ</span></span></td>\r\n			<td rowspan="2">\r\n			<p style="text-align:center"><span style="font-size:14px"><img alt="" src="http://www.mteamwork.com/images/stories/Article/201105/4work.jpg" style="height:277px; width:258px" /></span></p>\r\n			</td>\r\n			<td><span style="font-size:14px"><strong><span style="background-color:transparent; color:rgb(255, 102, 0)">B (Business Owner) - เจ้าของธุรกิจ</span></strong><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ต้องมีเงินทุน</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- หาคนเก่งๆ มาทำงานให้</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ไม่ทำก็มีรายได้</span><br />\r\n			<strong>ธุรกิจมีหลายประเภท</strong><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- เปิดบริษัท</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ซื้อแฟรนไซน์</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ทำการตลาดแบบเครือข่าย</span></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td><span style="font-size:14px"><span style="color:rgb(255, 102, 0); font-family:arial,helvetica,sans-serif"><strong>S (Self-employed) - ทำธุรกิจส่วนตัว</strong></span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ขายเวลาแลกกับเงิน จ้างตัวเองทำงาน</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ชอบคิดเองทำเอง, ทำทุกอย่างด้วยตัวเอง</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ขาดประสบการณ์</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- เจอคู่แข่งที่มีทุนหนากว่า</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- อาจจะทนทำเพราะชอบอิสระ แต่ไม่มีอิสรภาพ</span></span></td>\r\n			<td><span style="font-size:14px"><span style="color:rgb(255, 102, 0); font-family:arial,helvetica,sans-serif"><strong>I (Investor) - นักลงทุน</strong></span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ไม่ได้ทำงานเพื่อเงิน</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- มองผลตอบแทนจากการปันผล ดอกเบี้ย</span><br />\r\n			<span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">- ซื้อกิจการมาปรับปรุง แล้วขายต่อ</span></span></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table align="center" border="0" cellpadding="5" cellspacing="5" style="width:100%">\r\n	<tbody>\r\n		<tr>\r\n			<td><span style="font-size:14px"><strong><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent"><span style="background-color:transparent"><span style="background-color:transparent"><span style="background-color:transparent; color:rgb(255, 102, 0)">แนวความคิดของคนฝั่งซ้าย</span></span></span></span></span></strong></span></td>\r\n			<td><span style="font-size:14px"><strong><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent"><span style="background-color:transparent"><span style="background-color:transparent"><span style="background-color:transparent; color:rgb(255, 102, 0)">แนวความคิดของคนฝั่งขวา</span></span></span></span></span></strong></span></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<ul>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">มี ความกลัว เป็นตัวขับเคลื่อน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ยึดติดกับงานประจำ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">รายได้จำกัด</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">คิดเองทำเอง</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ไม่มีเป้าหมายในชีวิต</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">มองเห็นอุปสรรค</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ไม่เข้าใจคำว่า ทรัพย์สิน หนี้สิน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ทำงานเพื่อเงิน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">คิดถึงความเสี่ยง</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ยึดติดกับสิ่งเก่า</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ลงทุนในสิ่งที่เห็น</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ไม่มีแผนงาน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ดำเนินชีวิตด้วยตัวเอง</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบออกความเห็น</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบมีเงินสดเยอะๆ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบเป็นผู้จัดการ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบแสดงตัวว่าเก่ง</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบวิธีการ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบการเฉลี่ย (ขจัดความเสี่ยง)</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ถูกระบบความคุม</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">เป็นส่วนหนึ่งของระบบ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">เรียนเพื่อประกาศนียบัตร</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบเป็นผู้เชี่ยวชาญ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ทำงานเพื่อคนอื่น</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">อยากทำบุญแต่ไม่มีงบ</span></span></li>\r\n			</ul>\r\n			</td>\r\n			<td>\r\n			<ul>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">มี ความฝัน (ความใฝ่ฝัน) เป็นตัวขับเคลื่อน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">พยายามสร้างงาน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">รายได้ไม่จำกัด</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ทำงานเป็นทีม</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">มีเป้าหมายชัดเจน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">มองเห็นโอกาส</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">เข้าใจคำว่าทรัพย์สิน - หนี้สิน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ใช้เงินทำงาน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">คิดถึงความน่าเสี่ยง</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">เรียนรู้สิ่งใหม่</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ลงทุนในสิ่งที่คิด</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">มีแผนงานชัดเจน</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">มีที่ปรึกษา</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบหาความจริง</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบมี กระแสเงินสด สม่ำเสมอ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบเป็นผู้นำ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบมองหาคนเก่ง</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบวิธีคิด</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบการจดจ่อ (Focus)</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ความคุมระบบ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">เป็นเจ้าของระบบ</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">เรียนเพื่อหาความรู้</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ชอบเป็นผู้รอบรู้</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">สร้างงานเพื่อคนอื่น</span></span></li>\r\n				<li><span style="font-size:14px"><span style="color:rgb(68, 68, 68); font-family:arial,helvetica,sans-serif">ทำบุญทุกครั้งที่มีโอกาส</span></span></li>\r\n			</ul>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent; font-size:14pt"><span style="background-color:transparent; color:rgb(128, 0, 128)">แล้วคุณเลือกอยู่ฝั่งไหน ?</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:14px"><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent">งานประจำ ไม่ทำให้ร่ำรวยได้ มีแต่หนี้ ถ้าต้องการความสำเร็จ ต้องเป็นเจ้าของธุรกิจ ธุรกิจมี 2 ประเภท คือ รวยแต่หยุดทำไม่ได้ กับรวยแล้วพักได้โดยรายได้ไม่หยุด</span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent">1. กิจการใหญ่ (ซีพี , AIS)</span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent">2. เจ้าของแฟรนไชส์ (แมคโดนัลด์ , 7-11)</span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent">3.&nbsp;<span style="background-color:transparent; color:rgb(255, 0, 0)"><strong><span style="background-color:transparent; color:rgb(128, 0, 128)">ธุรกิจเครือข่าย</span></strong></span></span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent">- การตลาดเครือข่าย เป็นเสมือนโรงเรียนสอนนักธุรกิจ ที่ช่วยให้คุณย้ายฝั่งได้ง่าย ได้ผล และปลอดภัยที่สุด</span></span></span></p>\r\n\r\n<p><span style="font-size:14px"><span style="background-color:transparent; font-family:arial,helvetica,sans-serif"><span style="background-color:transparent">- บริษัทเครือข่ายการตลาดที่ดีจะต้องมีระบบพัฒนาตัวคุณอย่างสมบูรณ์แบบ (โดยสอนให้คุณเป็นนักธุรกิจ เพื่อเป็นเจ้าของธุรกิจ ไม่ใช่สอนให้คุณเป็นเซลส์แมนหรือเพียงทำให้คุณมีรายได้ไปวันๆ บนความมั่นคงที่ไม่แน่นอน)</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n', '2013-09-19 04:54:19', '1', 6),
(7, 'ดินเนอร์ครูซ GOLD Moving Up', '5319d5bfb57877.jpg', 'รางวัลสำหรับนักธุรกิจที่สามารถขึ้นตำแหน่งตั้งแต่ระดับโกลด์ขึ้นไป ร่วมประทับใจกับดินเนอร์ ', '<h1>ดินเนอร์ครูซ GOLD Moving Up</h1>\r\n\r\n<div class="categories" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><a href="http://th.monavie.com/th/news/________________________" style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(126, 162, 0); text-decoration: none; ">อีเว้นท์</a></div>\r\n\r\n<div class="main-img" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><img alt="" src="http://th.monavie.com/sites/th.monavie.com/files/styles/media_blog_image_layout/public/managed/blog/slider-th-season-two_0.jpg" style="border:0px; font-size:12px; font:inherit; margin:0px; outline:none; padding:0px; vertical-align:baseline" title="" /></div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<p><span style="font-size:14px">รางวัลสำหรับนักธุรกิจที่สามารถขึ้นตำแหน่งตั้งแต่ระดับโกลด์ขึ้นไป ร่วมประทับใจกับดินเนอร์ พร้อมดื่มด่ำบรรยากาศยามค่ำคืนอันแสนโรแมนติคกลางแม่น้ำเจ้าพระยา บนเรือสุดหรู กับงานปาร์ตี้ Superstar<br />\r\n<strong>โปรโมชั่นพิเศษเฉพาะรอบคำนวณโบนัสที่&nbsp;</strong><strong>399-405&nbsp;</strong><strong>เท่านั้น&nbsp;</strong>รายละเอียดการทำคุณสมบัติ<strong>:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">สำหรับนักธุรกิจที่ปัจจุบันดำรงคุณสมบัติโกลด์เป็นตำแหน่งสูงสุด: ต้องดำรงคุณสมบัติ &ldquo;Paid as&rdquo; Gold 1 ครั้ง ในรอบคำนวณโบนัสที่ 399-405</span></li>\r\n	<li><span style="font-size:14px">สำหรับนักธุรกิจที่เคยมีตำแหน่งสูงสุดตั้งแต่รูบี้ขึ้นไป: ต้องดำรงคุณสมบัติตำแหน่งสูงสุดเดิมที่เคยได้รับ 1 ครั้ง ในรอบคำนวณโบนัสที่ 399-405</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>ระยะเวลาการทำคุณสมบัติ<span dir="RTL">&nbsp;</span><span dir="RTL">:</span></strong><span dir="LTR">&nbsp;</span>15 ก.ค. - 3 พ.ย. 2555 (รอบคำนวณโบนัสที่ 394&ndash;409)<br />\r\n<strong>รวมระยะเวลาการทำคุณสมบัติ<span dir="RTL">&nbsp;</span><span dir="RTL">:</span></strong><span dir="LTR">&nbsp;</span>16 รอบคำนวณโบนัส<br />\r\n<strong>กำหนดการเดินทาง:&nbsp;</strong>ธันวาคม 2555<br />\r\n<br />\r\n<strong>รายละเอียดการทำคุณสมบัติ</strong><strong>:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">ต้องมียอดส่วนตัวอย่างน้อย 100 PV (Active)</span></li>\r\n	<li><span style="font-size:14px">มีการแนะนำตรง ซ้าย 1 ขวา 1 ที่มียอดส่วนตัวอย่างน้อย<span dir="RTL">&nbsp;</span>50 PV (Qualified)</span></li>\r\n	<li><span style="font-size:14px">ต้องดำรงคุณสมบัติ Gold (ต้องมีคะแนนอย่างน้อยข้างละ 5,000 PV) หรือสูงกว่า ในรอบที่กำหนด</span></li>\r\n	<li><span style="font-size:14px">สำหรับนักธุรกิจใหม่หรือไม่เคยดำรงคุณสมบัติโกลด์ (New Gold): ต้องดำรงตำแหน่ง Gold 1 ครั้ง ในช่วงเวลาที่กำหนด</span></li>\r\n	<li><span style="font-size:14px">สำหรับนักธุรกิจที่เคยมีตำแหน่งสูงสุดตั้งแต่โกลด์ขึ้นไป (Gold &amp; Above): ต้องทำคุณสมบัติขึ้นตำแหน่งสูงกว่าตำแหน่งสูงสุดเดิมที่เคยได้รับ ในช่วงเวลาที่กำหนด</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>เงื่อนไขในการเข้าร่วมดินเนอร์ครูซ&nbsp;</strong><strong>GOLD Moving Up</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">ต้องมียอดส่วนตัวอย่างน้อย 100 PV (Active)</span></li>\r\n	<li><span style="font-size:14px">มีการแนะนำตรง ซ้าย 1 ขวา 1 ที่มียอดส่วนตัวอย่างน้อย<span dir="RTL">&nbsp;</span>50 PV (Qualified)</span></li>\r\n	<li><span style="font-size:14px">นักธุรกิจโมนาวีที่ได้รับรางวัล จะต้องเป็นผู้ที่ปฎิบัติตามกฎจรรยาบรรณ เงื่อนไข และข้อบังคับของทางบริษัทอย่างถูกต้อง</span></li>\r\n	<li><span style="font-size:14px">นักธุรกิจจะได้รับการยืนยันสิทธิ์การเข้าร่วมการสัมมนา หลังจากที่ผ่านการตรวจสอบจากบริษัทแล้วเท่านั้น</span></li>\r\n	<li><span style="font-size:14px">การเดินทางเข้าร่วมสัมมนา สงวนสิทธิ์สูงสุด 2 ที่นั่งต่อรหัสเท่านั้น</span></li>\r\n	<li><span style="font-size:14px">ผู้ที่ผ่านคุณสมบัติการเดินทาง จะต้องเป็นผู้ที่เป็นเจ้าของรหัส ID เท่านั้น ไม่สามารถโอนสิทธิ์ให้ผู้อื่น หรือเปลี่ยนเป็นเงินสดได้</span></li>\r\n	<li><span style="font-size:14px">การขึ้นตำแหน่งจะนับเฉพาะคะแนนที่เกิดขึ้นในประเทศไทยเท่านั้น</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>หมายเหตุ:</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">สิทธิ์ที่ 2 ต้องเป็นนักธุรกิจโมนาวีหรือบุคคลในครอบครัวที่มีอายุ 18 ปีขึ้นไป</span></li>\r\n	<li><span style="font-size:14px">บริษัทขอสงวนสิทธิ์ในการพิจารณาสิทธิ์ในการเข้าร่วมเดินทางสัมมนาแต่เพียงผู้เดียว</span></li>\r\n	<li><span style="font-size:14px">บริษัทขอสงวนสิทธิ์ในการเปลี่ยนแปลงโปรโมชั่นโดยมิต้องแจ้งให้ทราบล่วงหน้า</span></li>\r\n</ul>\r\n</div>\r\n', '2013-09-19 04:55:47', '1', 5),
(8, 'Rise to Japan', 'f4f556d6504061.jpg', 'บินฟรีกับโมนาวี 4 วัน 3 คืน เพียงดำรงคุณสมบัติ“Paid-as” Bronze มีสิทธิ์เข้าร่วมเดินทางสัมมนา ณ ดินแดนปลาดิบ ประเทศญี่ปุ่นพร้อมรับประสบการณ์สุดพิเศษ', '<p>&nbsp;</p>\r\n\r\n<h1>Rise to Japan</h1>\r\n\r\n<div class="categories" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><a href="http://th.monavie.com/th/news/________________________" style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(126, 162, 0); text-decoration: none; ">อีเว้นท์</a></div>\r\n\r\n<div class="main-img" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><img alt="" src="http://th.monavie.com/sites/th.monavie.com/files/styles/media_blog_image_layout/public/managed/blog/slider-TW-rise-to-japan.jpg" style="border:0px; font-size:12px; font:inherit; margin:0px; outline:none; padding:0px; vertical-align:baseline" title="" /></div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<p><span style="font-size:14px">บินฟรีกับโมนาวี 4 วัน 3 คืน เพียงดำรงคุณสมบัติ&ldquo;Paid-as&rdquo; Bronze มีสิทธิ์เข้าร่วมเดินทางสัมมนา ณ ดินแดนปลาดิบ ประเทศญี่ปุ่นพร้อมรับประสบการณ์สุดพิเศษ<br />\r\n<strong>ระยะเวลาการทำคุณสมบัติ:</strong>&nbsp;27 ก.ค. &nbsp;2556 &ndash; 26 ต.ค. 2556 (รอบคำนวณโบนัสที่ 448&ndash;460)<br />\r\n<strong>รวมระยะเวลาการทำคุณสมบัติ:</strong>&nbsp;&nbsp;13 &nbsp;รอบคำนวณโบนัส<br />\r\n<strong>กำหนดการเดินทาง : &nbsp;</strong>28 พ.ย. &ndash; 1 ธ.ค 2556 (30 พ.ย. รวมงานประชุมใหญ่ ณ โยโกฮามา)<br />\r\n<br />\r\n<strong>ผู้ที่เข้าร่วมการเดินทางจะต้องมีคุณสมบัติดังต่อไปนี้ (รับฟรี&nbsp;</strong><strong>1&nbsp;</strong><strong>ที่นั่ง)</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">ทำคะแนนที่เกิดขึ้นใหม่ในข้างขาอ่อน (weak leg) ขั้นต่ำ 2,000 คะแนนต่อสัปดาห์ (Paid-as ตำแหน่ง บรอนซ์) เป็นเวลา 8 สัปดาห์ (ไม่จำเป็นต้องเป็นสัปดาห์ที่ติดต่อกัน) ในช่วงเวลาที่กำหนด</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>หากท่านทำคะแนนข้างอ่อนต่ำกว่า&nbsp;</strong><strong>2,000 คะแนนต่อสัปดาห์ ท่านสามารถสะสมคะแนนรวมข้างอ่อนที่เกิดขึ้นใหม่ 18,000 คะแนน โดยบริษัทจะนับคะแนนจาก 8 สัปดาห์ที่ดีที่สุดในช่วงเวลาโปรโมชั่น (สัปดาห์ที่ 448-460) โดยจะนำเฉพาะคะแนนของสัปดาห์ที่มีคะแนนเกิดขึ้นใหม่มากกว่า 1,000 คะแนน และนับสูงสุดไม่เกิน 4,000 คะแนน ต่อสัปดาห์เพื่อคำนวณการผ่านคุณสมบัติได้</strong><br />\r\nกรณีทำขั้นต่ำ 2,000 คะแนนติดต่อกันไม่ถึง 8 สัปดาห์แต่เกิน 5 สัปดาห์ หรือคะแนนรวมมากกว่า 12,000 คะแนนใน 5 สัปดาห์ในช่วงเวลาโปรโมชั่น ท่านจะได้รับสิทธิ์ในการร่วมทริป โดยที่ทางบริษัทจะรับผิดชอบค่าใช้จ่ายของท่านตลอด 4 วัน 3 คืนระหว่างอยู่ที่ประเทศญี่ปุ่น แต่ท่านจะต้องรับผิดชอบค่าใช้จ่ายในส่วนของค่าตั๋วเครื่องบินไป-กลับและค่าเดินทางจากท่าอากาศยานญี่ปุ่นถึงที่พักด้วยตัวท่านเอง</span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">ต้องมียอดส่วนตัวอย่างน้อย 100 &nbsp;PV (Active) ในช่วงเวลาที่กำหนดและตลอดจนวันเดินทาง</span></li>\r\n	<li><span style="font-size:14px">ต้องมีการแนะนำตรง ซ้าย 1 ขวา 1 ที่มียอดส่วนตัวอย่างน้อย 50 PV (Qualified)</span></li>\r\n	<li><span style="font-size:14px">ต้องสปอนเซอร์ตรงนักธุรกิจใหม่ 5 ท่าน โดยต้องมีการสั่งซื้อครั้งแรกด้วยชุดผลิตภัณฑ์ดูแลผิวโมนาวีเอ็นวี (MonaVie NV Collection Set) ขั้นต่ำ 100 PV (Active) 3 รหัส และน้ำโมนาวี 2 รหัส ในช่วงเวลาที่กำหนด</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>เงื่อนไขการเข้าร่วม</strong><strong>เดินทางสัมมนา</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">นักธุรกิจโมนาวีที่ได้รับรางวัลท่องเที่ยวต้องเป็นผู้ที่ปฎิบัติตามกฎ จรรยาบรรณ เงื่อนไข และข้อบังคับของทางบริษัทอย่างถูกต้อง</span></li>\r\n	<li><span style="font-size:14px">บริษัทจะยืนยันสิทธิ์เข้าร่วมทริปของนักธุรกิจหลังจากที่ได้ตรวจสอบอย่างละเอียดแล้วเท่านั้น</span></li>\r\n	<li><span style="font-size:14px">ผู้ที่จะเดินทางต้องเป็นเจ้าของรหัสนักธุรกิจที่ผ่านคุณสมบัติการเข้าร่วมทริปเท่านั้น ไม่สามารถโอนสิทธิ์ให้ผู้อื่นหรือเปลี่ยนเป็นเงินสดได้</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">การเข้าร่วมทริปสงวนสิทธิ์สูงสุด 2 ที่นั่งต่อรหัสเท่านั้น โดยสามารถมอบสิทธิ์ที่&nbsp;<strong>2&nbsp;</strong>ให้กับท่านใดก็ได้</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">การจะได้รับ 2 สิทธิ์นั้นท่านต้องดำรงคุณสมบัติเป็น 2 เท่าในช่วงเวลาที่กำหนด</span></li>\r\n</ul>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">ท่านจะมีสิทธิ์ในการซื้อตั๋วใบที่ 2 ตามเงื่อนไขเมื่อท่านทำคุณสมบัติใบแรกสมบูรณ์แล้วเท่านั้น</span></li>\r\n	<li><span style="font-size:14px">กรณีมีคะแนนข้างอ่อนสูงกว่า 4,000 คะแนนต่อสัปดาห์จะนับคะแนนเพื่อคำนวณคุณสมบัติสูงสุดที่&nbsp; 4,000 คะแนน</span></li>\r\n	<li><span style="font-size:14px">การทำคุณสมบัตินับเฉพาะคะแนนที่เกิดขึ้นในประเทศไทยเท่านั้น</span></li>\r\n</ul>\r\n\r\n<p><span style="font-size:14px"><strong>หมายเหตุ</strong></span></p>\r\n\r\n<ul>\r\n	<li><span style="font-size:14px">นักธุรกิจที่ผ่านคุณสมบัติการเข้าร่วมทริปจะถูกหักภาษี ณ ที่จ่ายจากมูลค่ารางวัลท่องเที่ยว</span></li>\r\n	<li><span style="font-size:14px">ผู้ประสงค์เข้าร่วมทริปต้องยื่นหนังสือแจ้งความจำนงภายในวันศุกร์ที่ 25 ตุลาคม 56 มิฉะนั้นจะถือว่าท่านไม่ได้ทำคุณสมบัติครบถ้วนตามกำหนด</span></li>\r\n	<li><span style="font-size:14px">หากท่านผ่านคุณสมบัติแต่ไม่ต้องการเข้าร่วมทริปท่านต้องแจ้งล่วงหน้าอย่างน้อย 15 วันก่อนวันเดินทาง หากท่านไม่ปฎิบัติตามท่านจะถูกตัดสิทธิ์ในการเข้าร่วมทริปครั้งต่อไป</span></li>\r\n	<li><span style="font-size:14px">หากท่านไม่สามารถเข้าร่วมทริปได้ไม่ว่าด้วยกรณีใดก็ตาม บริษัทขอสงวนสิทธิ์ในการชดเชยเป็นเงินสด</span></li>\r\n	<li><span style="font-size:14px">บริษัทขอสงวนสิทธิ์ในการพิจารณาสิทธิ์ในการเข้าร่วมทริปแต่เพียงผู้เดียว</span></li>\r\n	<li><span style="font-size:14px">บริษัทขอสงวนสิทธิ์ในการเปลี่ยนแปลงรายละเอียดและเงื่อนไขโดยไม่ต้องแจ้งให้ทราบล่วงหน้า</span></li>\r\n</ul>\r\n</div>\r\n', '2013-09-19 04:57:12', '1', 5),
(9, 'ขั้นตอนที่ 4: ผลัดเซลล์ผิวเสื่อมสภาพด้วยโมนาวีเอ็นวีมูสชาวเวอร์สครับ', 'bad693067abaa7.jpg', 'ฟื้นคืนชีวิตให้ผิวนุ่มเนียนด้วยโมนาวีเอ็นวีมูสชาวเวอร์สครับ', '<h1>ขั้นตอนที่ 4: ผลัดเซลล์ผิวเสื่อมสภาพด้วยโมนาวีเอ็นวีมูสชาวเวอร์สครับ</h1>\r\n\r\n<div class="categories" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><a href="http://th.monavie.com/th/news/___________________________" style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: 0px; color: rgb(126, 162, 0); text-decoration: none; ">ผลิตภัณฑ์</a></div>\r\n\r\n<div class="main-img" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; "><img alt="" src="http://th.monavie.com/sites/th.monavie.com/files/styles/media_blog_image_layout/public/managed/blog/slider-thailand-step-four-th.jpg" style="border:0px; font-size:12px; font:inherit; margin:0px; outline:none; padding:0px; vertical-align:baseline" title="" /></div>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(86, 86, 86); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; ">\r\n<p>&nbsp;</p>\r\n\r\n<p><span style="font-size:14px">ฟื้นคืนชีวิตให้ผิวนุ่มเนียนด้วยโมนาวีเอ็นวีมูสชาวเวอร์สครับ</span></p>\r\n\r\n<p><span style="font-size:14px">ผลิตภัณฑ์ทำความสะอาดผิวกายทั่วไปสามารถชะล้างสิ่งสกปรก แต่ไม่สามารถผลัดเซลล์ผิวเสื่อมสภาพได้ จึงควรใช้ผลิตภัณฑ์ที่ช่วยผลัดเซลล์ผิวเสื่อมสภาพ เพื่อเผยผิวใหม่ที่สดใสและดูอ่อนเยาว์</span></p>\r\n\r\n<p><span style="font-size:14px">ส่วนผสมสูตรอ่อนโยนในเอ็นวีมูสชาวเวอร์สครับช่วยผลัดเซลล์ผิวเสื่อมสภาพ และสิ่งสกปรกตกค้าง ให้ผิวนุ่มและกระจ่างใสขึ้น สครับสูตรเกลือเนื้อนุ่มละมุนผิวนี้มีสารต้านอนุมูลอิสระอย่างน้ำมันจากผลอา ไซอิ แอนดิโรบา และอินคาจากธรรมชาติ ผสานด้วยเทคโนโลยีไลโปโซม นวัตกรรมแห่งวิทยาการ เพื่อส่งผ่านสารสำคัญลงสู่ผิว รวมทั้งโปรคอลลาเจนเปปไทด์ที่ช่วยคืนความยืดหยุ่นให้ผิวเนียนนุ่มดุจแพรไหม ส่องประกายผิวสุขภาพดี</span></p>\r\n\r\n<p><span style="font-size:14px"><strong>คุณสมบัติ</strong></span></p>\r\n\r\n<p><span style="font-size:14px">&bull; ปรับสีผิวและสภาพผิว</span></p>\r\n\r\n<p><span style="font-size:14px">&bull; คงความเนียนนุ่มให้กับผิว</span></p>\r\n\r\n<p><span style="font-size:14px">&bull; ป้องกันการทำลายจากอนุมูลอิสระและสัญญาณความร่วงโรยแห่งวัย</span></p>\r\n\r\n<p><span style="font-size:14px">มอบความอ่อนเยาว์ สดใส และเนียนนุ่มให้กับผิวกายด้วยเอ็นวีมูสชาวเวอร์สครับ</span></p>\r\n\r\n<p><span style="font-size:14px">ท่านสามารถซื้อชุดผลิตภัณฑ์บำรุงผิวโมนาวีเอ็นวีได้ในราคาพิเศษที่ 5,400 บาท ก่อนวันจำหน่ายอย่างเป็นทางการถึง 5 วัน ได้ในวันพุธที่ 27&nbsp; มิถุนายน พ<strong>.</strong>ศ<strong>.&nbsp;</strong>2555 เวลา 10.00-16.00 นาฬิกา ที่สำนักงานโมนาวีประเทศไทย<strong>*&nbsp;</strong>และขอเชิญร่วมอีเว้นท์เปิดตัวผลิตภัณฑ์ที่ศูนย์การค้าเทอร์มินอล 21 ในเวลา 17.30-19.30 น.</span></p>\r\n\r\n<p><span style="font-size:14px">* จะคำนวณ 100 พีวีที่ท่านได้รับจากการซื้อผลิตภัณฑ์ไว้ในสัปดาห์ธุรกิจที่ 392&nbsp;</span></p>\r\n</div>\r\n', '2013-09-19 04:59:24', '1', 1),
(10, 'มนาวี อาซาอิ-เพอร์เพิล', 'cc878ea6da53c3.jpg', 'มนาวี อาซาอิ-เพอร์เพิลเป็นส่วนผสมรสชาติเยี่ยมที่ลงตัวของอาซาอิเบอร์รี่สายพันธุ์บราซิลกับผลไม้ที่มีประโยชน์ต่อร่างกายรวม 18 ', '<div class="pre-content ---" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; color: rgb(255, 255, 255); font-family: futura-pt-n4, futura-pt, Arial, sans-serif; background-color: rgb(39, 39, 39); ">\r\n<h2>มนาวี อาซาอิ-เพอร์เพิล</h2>\r\n\r\n<div style="margin: 0px; padding: 0px; border: 0px; font-size: 12px; font: inherit; vertical-align: baseline; outline: none; "><img alt="" src="http://th.monavie.com/sites/th.monavie.com/files/styles/product_subpages_main_product_image/public/managed/product/essential-product-improvehealth.jpg" style="border:0px; font-size:12px; font:inherit; margin:0px; outline:none; padding:0px; vertical-align:baseline" /></div>\r\n</div>\r\n\r\n<div class="post ---" id="---" style="margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: none; ">\r\n<p style="text-align: justify; ">โ<span style="font-size:14px">มนาวี อาซาอิ-เพอร์เพิลเป็นส่วนผสมรสชาติเยี่ยมที่ลงตัวของอาซาอิเบอร์รี่สายพันธุ์บราซิลกับผลไม้ที่มีประโยชน์ต่อร่างกายรวม 18 ชนิด โมนาวีอาศัยวิทยาการอันล้ำหน้าเพื่อนำสิ่งดีๆ จากธรรมชาติมาเสริมคุณค่าทางโภชนาการให้ทุกช่วงวัย</span></p>\r\n\r\n<p style="text-align: justify; "><span style="font-size:14px">พัฒนาขึ้นบนหลักการของความสมดุลย์ ความหลากหลาย และความพอดี น้ำผลไม้เปี่ยมประสิทธิภาพนี้ช่วยสร้างปราการต้านอนุมูลอิสระ ปกป้องร่างกายลึกถึงระดับเซลล์ ชะลอกลไกความชรา เพิ่มไฟโตนิวเทรียนท์ให้ร่างกาย ให้คุณค่าสารอาหารครบถ้วน และช่วยเสริมสร้างสุขภาพที่ดี เหมาะสำหรับผู้รักสุขภาพ สามารถดื่มได้เป็นประจำทุกวัน</span></p>\r\n</div>\r\n', '2013-09-19 05:00:41', '1', 1);

